package codingPractice;

public class BinaryJumps {

    public static void main (String[] args) {

        String game = "";
        char lastTile = game.charAt(game.length() - 1);
        int result = 1;
        int current = result;

        for(int i=0; i<game.length(); i++) {
            if (game.charAt(i) != lastTile) {
                current++;
            } else {
                if (current > result) {
                    result = current;
                }
                current = 1;
            }
        }

        System.out.println(result);
    }

}
